html, body {
    margin: 0px;
    background: #073b4c;
    font-family: 'Lucida Sans', 'Lucida Sans Regular', 'Lucida Grande', 'Lucida Sans Unicode', Geneva, Verdana, sans-serif;
    color: white;
}

#login {
    padding: 1rem;
    max-width: 800px;
    margin: 0 auto;
}

#login form {
    display: grid;
    grid-template-columns: 30% auto;
    grid-gap: 0.5rem;
}

input {
    color: white;
    margin: 0.5rem;
    padding: 0.5rem;
    border: none;
    border-bottom: solid #ffd166 2px;
    background-color: rgba(255, 255, 255, 0.1);
}

input.invalid {
    border-bottom: solid #ef476f 2px;
}

.button {
    border: solid #ffd166 2px;
    border-radius: 4px;
    padding: 0.3rem;
    text-align: center;
}

.button:hover {
    background-color: #ffd166;
    color: black;
}

#login form #connect {
    grid-column-start: 1;
    grid-column-end: 3;
    font-size: 1.1rem;
}
